import React from 'react'
import AppContext from "../../context/context";
import {v4 as uuidv4} from 'uuid';
import styles from './Form.module.scss'
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {Formik, Form} from 'formik';
import accept from '../../asstets/img/accept.svg'
import decline from '../../asstets/img/decline.svg'
import styled from "styled-components";

const FormWrapper = styled.div`
      padding: 70px 80px 50px;
      top: 50%;
      transform: translateY(-50%);
      left: 0;
      right: 0;
      margin: 0 auto;
      width: 75vw;
      //height: 90vh;
      background-color: #FFFAF3;
      position: fixed;
`;

const FormItem = styled.div`
      width: 100%;
      margin: 24px 0;
      position: relative;
      flex-shrink: 0;
`
const StyledInput = styled.input`
    display: block;
    appearance: none;
    outline: 0;
    border: 1px solid white;
    background-color:white;
    width: 250px;
    border-radius: 3px;
    padding: 10px 15px;
    margin: 0 auto 10px auto;
    text-align: center;
    font-size: 18px;
    color: black;

    transition-duration: 0.25s;
    font-weight: 300;

    &:hover{
      background-color: white;
    }

    &:focus{
      background-color: white;
      width: 300px;
      color: black;
  }
`
const StyledLabel = styled.label`
      background-color: ${({theme}) => theme.colors.blue};
      position: absolute;
      top: 3px;
      left: 0;
      transition: 0.2s ease-out all;
      font-size: 16px;
      height: 70px;
      width: 500px;
`


class Formm extends React.Component {

    state = {
        id: "",
        name: "",
        currentQuantity: "",
        minimalQuantity: "",
        maximalQuantity: "",
    };

    handleInputChange = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };

    handleSubmitForm = e => {
        const {addItem} = this.props
        addItem(e, this.state);
        this.setState({
            id: 0,
            name: "",
            currentQuantity: "",
            minimalQuantity: "",
            maximalQuantity: "",
        });
    };

    notify = () => {
        toast.success("Dodano", {
            position: toast.POSITION.TOP_CENTER
        });
    }

    render() {

        return (
            <AppContext.Consumer>
                {context => (
                    <FormWrapper>
                        <h2>Dodaj nowy produkt</h2>
                        <Formik
                            initialValues={{name: 'xxx', currentQuantity: '', minimalQuantity: '', maximalQuantity: ''}}
                            onSubmit={this.handleSubmitForm}
                        >
                            {() => (
                                <Form>
                                    <FormItem>
                                        <StyledInput className={styles.input}
                                                     onChange={this.handleInputChange}
                                                     name="name"
                                                     type="text"
                                                     value={this.state.name}
                                                     placeholder=""/>
                                        <StyledLabel className={styles.label} htmlFor="currentQuantity">
                                            name
                                        </StyledLabel>
                                    </FormItem>
                                    <FormItem>

                                        <StyledInput className={styles.input}
                                                     onChange={this.handleInputChange}
                                                     name="currentQuantity"
                                                     type="number"
                                                     value={this.state.currentQuantity}
                                                     placeholder=""/>
                                        <StyledLabel className={styles.label} htmlFor="currentQuantity">
                                            current quantity
                                        </StyledLabel>
                                    </FormItem>
                                    <div className={styles.formItem}>
                                        <StyledInput className={styles.input}
                                                     onChange={this.handleInputChange}
                                                     name="minimalQuantity"
                                                     type="number"
                                                     value={this.state.minimalQuantity}
                                                     placeholder=""/>
                                        <StyledLabel className={styles.label} htmlFor="minimalQuantity">
                                            minimal Quantity
                                        </StyledLabel>
                                    </div>
                                    <FormItem>
                                        <StyledInput className={styles.input}
                                                     onChange={this.handleInputChange}
                                                     name="maximalQuantity"
                                                     type="number"
                                                     value={this.state.maximalQuantity}
                                                     placeholder=""/>
                                        <StyledLabel className={styles.label} htmlFor="maximalQuantity">
                                            maximal quantity
                                        </StyledLabel>
                                    </FormItem>
                                    <button
                                        onClick={() => this.notify(this.state.name)}
                                        type="submit" className={styles.button}>add new item
                                    </button>
                                    <ToastContainer autoClose={1200}/>
                                </Form>
                            )}
                        </Formik>
                    </FormWrapper>
                )}
            </AppContext.Consumer>
        )
    }
}
;


export default Formm;
