import React from 'react';
import List from '../components/organisms/List'
import AppContext from "../context/context";
import styled from "styled-components";
import {FormattedMessage} from "react-intl";
import ButtonIcon from "../components/atoms/ButtonIcon";
import beverages from "../asstets/categoryIcon/beverages.svg";
import bread from "../asstets/categoryIcon/bread.svg";
import dairy from "../asstets/categoryIcon/dairy.svg";
import meat from "../asstets/categoryIcon/meat.svg";
import pasta from "../asstets/categoryIcon/pasta.svg";
import spieces from "../asstets/categoryIcon/spieces.svg";
import sweets from "../asstets/categoryIcon/sweets.svg";
import vegetables from "../asstets/categoryIcon/vegetables.svg";

const ViewWrapper = styled.div`
     width: 75vw;
     margin: 0 auto;
     display: flex;
     justify-content: center;
     //flex-direction: column;
     flex-wrap: wrap;
     @media (max-width: ${({theme}) => theme.mobile}) {
        width: 100vw;
     }
`

const Heading = styled.h1`
     padding: 10px;
     color: ${({theme}) => theme.colors.blue};
     text-align: center;
     display: flex;
     justify-content: center;
     align-items: center;
     flex-direction: column;
     margin: 0 auto;
     width: 75vw;
     @media (max-width: ${({theme}) => theme.mobile}) {
        padding: 9rem 0 1rem;
        font-size: 26px;
        width: 100vw;
     }
`

const IconWrapper = styled.div`
    display: flex;
    flex-direction: column;
    text-align: center;
    margin: 20px 50px;
;
`

const Paragraph = styled.p`
    margin-top: 10px;
    font-size: 18px;
    font-weight: bold;
    color: ${({theme}) => theme.colors.blue};
`

const Icon = styled.img`
    margin: 10px 0 0 10px;
    width: 150px;
    height: 150px;
  // background-image: url(${({image}) => image});
`


const FilterView = () => (
    <AppContext.Consumer>
        {(context) => (
            <ViewWrapper>
                <Heading>
                    <FormattedMessage id="what in inventory"/>
                </Heading>
                <IconWrapper>
                    <Icon// onClick={() => sendMail()}
                        src={beverages}
                    />
                    <Paragraph>
                        Beverages
                    </Paragraph>
                </IconWrapper>

                <IconWrapper>
                    <Icon
                        // onClick={() => sendMail()}
                        src={bread}
                    />
                    <Paragraph>
                        Bread
                    </Paragraph>
                </IconWrapper>

                <IconWrapper>
                    <Icon// onClick={() => sendMail()}
                        src={dairy}
                    />
                    <Paragraph>
                        Dairy
                    </Paragraph>
                </IconWrapper>

                <IconWrapper>
                    <Icon
                        // onClick={() => sendMail()}
                        src={meat}
                    />
                    <Paragraph>
                        Meat
                    </Paragraph>
                </IconWrapper>

                <IconWrapper>
                    <Icon// onClick={() => sendMail()}
                        src={pasta}
                    />
                    <Paragraph>
                        Pasta
                    </Paragraph>
                </IconWrapper>

                <IconWrapper>
                    <Icon
                        // onClick={() => sendMail()}
                        src={spieces}
                    />
                    <Paragraph>
                        Spieces
                    </Paragraph>
                </IconWrapper>

                <IconWrapper>
                    <Icon
                        // onClick={() => sendMail()}
                        src={sweets}
                    />
                    <Paragraph>
                        Sweets
                    </Paragraph>
                </IconWrapper>

                <IconWrapper>
                    <Icon
                        // onClick={() => sendMail()}
                        src={vegetables}
                    />
                    <Paragraph>
                        Vegatables
                    </Paragraph>
                </IconWrapper>

            </ViewWrapper>
        )}
    </AppContext.Consumer>
);

export default FilterView
