import styled from "styled-components";

const LoginContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 50px 100px;
    background-color: #fff;
    border-radius: 5px;
    width: 25em;
    height: 40em;
    
.items {
  display: flex;
  flex-direction: column;
}

.items input {
  margin-bottom: 30px;
  padding: 2px 0px;
  padding-bottom: 5px;
  width: 250px;
  background-color: transparent;
  border: none;
  outline: none;
  border-bottom: 1.5px solid;
  border-bottom-color: grey;
  font-weight: 700;
  color: #585858;
  opacity: 0.55;
  }
  .button {
  padding: 20px 30px;
  margin-top: 30px;
  outline: none;
  border: none;
  font-size: medium;
  font-weight: 600;
  letter-spacing: 5px;
  border-radius: 5px;
  cursor: pointer;
  background: rgb(6, 215, 152);
  color: grey;

}

.title {
  margin-bottom: 50px;
  letter-spacing: 5px;
  text-align: center;
  opacity: 0.5;
}
`


const LoginForm = () => (
    <LoginContainer>
        <h2 className="title">Login</h2>

        <div className="items">
            <input type="text" id="form-text" placeholder="Username"/>
            <input type="password" id="form-password" placeholder="Password"/>

            <button className="button">Send</button>
            <a id="sign" type="submit" href="#">Sign Up</a>
        </div>
    </LoginContainer>
);

export default LoginForm;
