import React from 'react';
import AppContext from "../context/context";
import 'react-toastify/dist/ReactToastify.css';
import bag from '../asstets/img/bag.svg';
import styled from "styled-components";
import TableMarkup from "../components/molecules/Table";
import {FormattedMessage} from "react-intl";

const ListWrapper = styled.div`
      //padding: 70px 80px 50px;
      //top: 50%;
      //transform: translateY(-50%);
      //left: 0;
      //right: 0;
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
      margin: 0 auto;
      width: 60vw;
      font-size: 14px;
      //height: 90vh;
      //position: fixed;
       @media (max-width: ${({theme}) => theme.mobile}) {
        width: 100vw;
     }
`;

const Heading = styled.h1`
     padding: 10px;
     color: ${({theme}) => theme.colors.blue};
     text-align: center;
     display: flex;
     justify-content: center;
     align-items: center;
     flex-direction: column;
     margin: 0 auto;
      width: 60vw;
     @media (max-width: ${({theme}) => theme.mobile}) {
        padding: 9rem 0 1rem;
        font-size: 26px;
        width: 100vw;
     }
`

const Image = styled.img`
margin: 10px;
`

const ListView = () => (
    <ListWrapper>
        <Heading>
            <FormattedMessage id="shopping list"/>
        </Heading>
        <Image src={bag} alt="shoping bag"/>
        <AppContext.Consumer>
            {(context) => (
                (context.foodList.length) ?
                    <TableMarkup data={context.foodList}/>
                    :
                    <span>
                        <FormattedMessage id="empty list"/>
                    </span>
            )}
        </AppContext.Consumer>
    </ListWrapper>
);

export default ListView;
