import React from "react";
import styled from "styled-components";

const StyledTable = styled.table`
  border: none;
  border-collapse: collapse;
  border-radius: 20px;
  td,
  th {
        border: none;
        padding: 20px 50px;
        color: ${({theme}) => theme.colors.white};
        border-top: 1px solid ${({theme}) => theme.colors.blue}
  }
  td {
    padding: 20px 50px;
    vertical-align: top;
          color: black;

  }

  tbody tr {
    :nth-of-type(odd) {
      background-color: ${({theme}) => theme.colors.white};
      color: black;
    }
    :hover {
      background-color: ${({theme}) => theme.colors.green};
    }
  }
  thead > tr {
          background-color: ${({theme}) => theme.colors.blue};;
  }
`;

const TableMarkup = ({ data }) => (
// const TableMarkup = () => (
    <StyledTable>
        <colgroup>
            <col/>
            <col/>
            <col/>
        </colgroup>
        <thead>
        <tr>
            {/*{titles.map((title, index) => (*/}
            {/*    <th key={index}>{title}</th>*/}
            {/*))}*/}
            <th>#</th>
            <th>Nazwa</th>
            <th>Ilość</th>
            <th>Jednostka</th>

        </tr>
        </thead>
        <tbody>
        {data.map((item, index) => (
            <tr key={index}>
                {item.map((item, index) => (
                    <td key={index}>{index}</td>

                ))}
            </tr>
        ))}
        <tr>
            <td>1</td>
            <td>Bagietka</td>
            <td>5</td>
            <td>Sztuk</td>
        </tr>
        <tr>
            <td>2</td>
            <td>Bagietka</td>
            <td>5</td>
            <td>Sztuk</td>
        </tr>
        <tr>
            <td>3</td>
            <td>Bagietka</td>
            <td>5</td>
            <td>Sztuk</td>
        </tr>
        </tbody>
    </StyledTable>
);

export default TableMarkup;
