import React from 'react';
import styled from "styled-components";
import user from '../asstets/img/user.svg'
import settings from "../asstets/img/settings.svg";


const SettingsWrapper = styled.div`
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
      margin: 0 auto;
      width: 60vw;
      background-color: orange;
`;

const UserCard = styled.div`
      display: flex;
      justify-content: space-around;
      align-items: center;
`

const UserAvatarWrapper = styled.img`
      //margin: 0 auto;
      margin: 20px;
      width: 75px;
      height: 75px;
`
const UserMailWrapper = styled.div`
     display: flex;
     justify-content: center;
     align-items: center;
     flex-direction: column;
`
const Button = styled.button`
     display: flex;
     //margin: 50px;
     height: 70px;
     width: 230px;
     text-decoration: none;
     justify-content: center;
     align-items: center;
     color: ${({theme}) => theme.colors.white};
`

const SettingsCard = () => (

    <SettingsWrapper>
        <UserCard>
            <UserAvatarWrapper src={user} alt="Avatar icon"/>

            <UserMailWrapper>Jeste≈õ zalogowany jako Mati Mati @
                <Button> Wyloguj</Button>
            </UserMailWrapper>
        </UserCard>
    </SettingsWrapper>
)

export default SettingsCard;
