import i18next from "i18next";
import i18nextXHRBackend from "-xhr-backendi18next";
import {initReactI18next} from "react-i18next";


i18next
    .use(i18nextXHRBackend)
    .init({
        langs: ['en', 'pl'],
        fallbackLng: 'en',
        load: "all",
        whitelist: ["pl", "en"],
        debug: false
    });

export default i18n;
