export const theme = {
    colors: {
        yellow: '#FDE24F',
        pink: '#FF5470',
        green: '#00EBC7',
        // blue:'#007bff',
        darkblue:'#00214D',
        // blue:'#00214D',
        blue:'#363683',
        white: '#FFFFFE',
        gray: '#E5E5E5'
    },
    fontFamily: {
        primary: `'Red Hat Display', sans-serif`,
        heading: `'Reem Kufi', sans-serif`,
    },
};

export default theme;
