import React from 'react';
import styled from "styled-components";
import user from '../asstets/img/user.svg'
import {auth} from "../firebase/firebase";
import {useState} from "react/cjs/react.production.min";
import {theme} from "../theme/theme";
import context from "react-router/modules/RouterContext";
import {FormattedMessage, IntlProvider} from "react-intl";
// import {IntlProvider, FormattedMessage} from 'react-intl'

import English from "../views/en";


const SettingsWrapper = styled.div`
      display: flex;
      justify-content: center;
      //align-items: center;
      flex-direction: column;
      margin: 0 auto;
      padding: 20px;
      width: 50vw;
    @media (max-width: ${({theme}) => theme.mobile}) {
        width: 100vw;
     }
`;

const UserCard = styled.div`
      //padding-top: 10px;
      display: flex;
      justify-content: space-between;
      width: 40vw;
      margin: 0 auto;
      padding: 15px 0;
`

const UserAvatarWrapper = styled.img`
      width: 100px;
      height: 100px;
      display: flex;
      justify-content: center;
      margin: 0 auto;
`
const UserMailWrapper = styled.div`
     display: flex;
     //justify-content: center;
     align-items: center;
     flex-direction: column;
`
const Button = styled.button`
    //padding: 20px 30px;
    //margin-top: 30px;
    width: 170px;
    height: 50px;
    outline: none;
    border: none;
    font-size: 20px;
    font-weight: 600;
    letter-spacing: 5px;
    border-radius: 5px;
    cursor: pointer;
    background: ${({theme}) => theme.colors.pink};
    color: ${({theme}) => theme.colors.white};
`

const ButtonMode = styled.button`
    display: block;
    appearance: none;
    outline: 0;
    border: 1px solid white;
    width: 300px;
    border-radius: 3px;
    margin: 6px;
    text-align: center;
    font-size: 18px;
    color: black;
    transition-duration: 0.25s;
    font-weight: 300;
    background-color: ${({theme}) => theme.colors.gray};
        @media (max-width: ${({theme}) => theme.mobile}) {
          height: 50px;
  
    }
`

const OptionsWrapper = styled.div`
      width: 100%;
      display: flex;
      flex-direction: column;
      margin: 0 auto;
      //justify-content: center;
      //align-items: center;
`
const OptionsItem = styled.div`
      width: 100%;
      display: flex;
      justify-content: center;
               @media (max-width: ${({theme}) => theme.mobile}) {
          flex-direction: column;
          border: 1px solid   ${({theme}) => theme.colors.darkblue});
  
    }
`
const StyledLabel = styled.label`
      background-color: ${({theme}) => theme.colors.blue};
      color: ${({theme}) => theme.colors.white};
      text-align: center;
      justify-content: center;  
      transition: 0.2s ease-out all;
      font-size: 18px;
      height: 70px;
      width: 300px;
      margin: 6px;
      display: flex;
      text-decoration: none;
      align-items: center;
          @media (max-width: ${({theme}) => theme.mobile}) {
          height: 50px;
  
    }
`

const StyledInput = styled.input`
    display: block;
    appearance: none;
    outline: 0;
    border: 1px solid white;
    width: 300px;
    border-radius: 3px;
    margin: 6px;
    text-align: center;
    font-size: 18px;
    color: black;
    transition-duration: 0.25s;
    font-weight: 300;
    background-color: ${({theme}) => theme.colors.gray};
          @media (max-width: ${({theme}) => theme.mobile}) {
          height: 50px;
  
    }
    // &:focus{
    //       width: 300px;
    //
    //   background-color: #bcffb8;
    // }
    //
    // &:focus{
    //   width: 300px;
    //       background-color: ${({theme}) => theme.colors.gray};

  }
`
const StyledSelect = styled.select`
    display: block;
    appearance: none;
    outline: 0;
    border: 1px solid white;
    width: 300px;
    border-radius: 3px;
    margin: 6px;
    text-align: center;
    font-size: 18px;
    color: black;
    transition-duration: 0.25s;
    font-weight: 300;
    background-color: ${({theme}) => theme.colors.gray};
      text-align-last:center;
               @media (max-width: ${({theme}) => theme.mobile}) {
          height: 50px;
  
    }

    // &:focus{
    //       width: 300px;
    //
    //   background-color: #bcffb8;
    // }
    //
    // &:focus{
    //   width: 300px;
    //       background-color: ${({theme}) => theme.colors.gray};

  }
`

const Heading = styled.h1`
     //margin-top: 30px;
     padding: 10px;
     color: ${({theme}) => theme.colors.blue};
     text-align: center;
     display: flex;
     justify-content: center;
     align-items: center;
     flex-direction: column;
     margin: 0 auto;
     width: 75vw;
`

const Paragraph = styled.p`
    padding: 15px;
`


const SettingsCard = ({toggleViewMode, darkMode, handleChange, changeLanguage, language, locale}) => {




    return (
        <>
            <Heading>
                <FormattedMessage id="settings"/>
            </Heading>
            <SettingsWrapper>
                <UserCard>
                    <UserAvatarWrapper src={user} alt="Avatar icon"/>
                    <UserMailWrapper>
                        <Paragraph>
                            <FormattedMessage id="logged as"/>
                            {auth.currentUser.email}
                        </Paragraph>
                        <Button onClick={() => auth.signOut()}>
                            <FormattedMessage id="logout"/>
                        </Button>
                    </UserMailWrapper>
                </UserCard>
                <OptionsWrapper>

                    <OptionsItem>
                        <StyledLabel>
                            <FormattedMessage id="change language"/>
                        </StyledLabel>

                            <StyledSelect onChange={handleChange} defaultValue={locale}>

                                {/*<option >Change language</option>*/}
                                <option value="pl">Polski</option>
                                <option value="en">English</option>
                            </StyledSelect>

                    </OptionsItem>
                    <OptionsItem>
                        <StyledLabel>
                            {/*<StyledLabel onChange={handleSelect} defaultValue={locale}>*/}
                            <FormattedMessage id="dark mode"/>
                        </StyledLabel>
                        <StyledSelect onChange={toggleViewMode} defaultValue="off">
                            <option value="on" label="Włączony"/>
                            <option value="off" label="Wyłączony"/>
                        </StyledSelect>
                        {/*<ButtonMode onClick={() => toggleViewMode()}>*/}
                        {/*    {darkMode === "dark" ? "Włączony" : "Wyłączony"}*/}
                        {/*</ButtonMode>*/}
                    </OptionsItem>
                    <OptionsItem>
                        <StyledLabel htmlFor="currentQuantity">
                            <FormattedMessage id="add unit"/>
                        </StyledLabel>
                        <StyledInput/>
                    </OptionsItem>
                    <OptionsItem>
                        <StyledLabel >
                            <FormattedMessage id="add category"/>
                        </StyledLabel>
                        <StyledInput/>

                    </OptionsItem>
                </OptionsWrapper>
                {/*<IntlProvider locale={handleChange} messages={language}/>)*/}
            </SettingsWrapper>
        </>
    )
}

export default SettingsCard;
