import React from "react";
import styled from "styled-components";

const StyledTable = styled.table`
  caption-side: top;
  border: none;
  border-collapse: collapse;
  border-radius: 20px;
  caption-side: bottom;
  td,
  th {
        border: none;
        padding: 20px 50px;
        background-color: ${({theme}) => theme.colors.blue};
        color: ${({theme}) => theme.colors.white};
        border-top: 1px solid ${({theme}) => theme.colors.white}
  }
  td {
    padding: 20px 50px;
    vertical-align: top;
  }

  tbody tr {
    :nth-of-type(odd) {
          background-color: yellow;
    }
  }
  thead > tr {
          background-color: yellow;
  }
  caption {
    font-size: 0.9em;
    padding: 5px;
    font-weight: bold;
  }
`;

// const TableMarkup = ({ titles, data }) => (
const TableMarkup = () => (
    <StyledTable>
        <colgroup>
            <col/>
            <col/>
            <col/>
        </colgroup>
        <thead>
        <tr>
            {/*{titles.map((title, index) => (*/}
            {/*    <th key={index}>{title}</th>*/}
            {/*))}*/}
            <th>Nazwa</th>
            <th>Ilość</th>
            <th>Jednostka</th>

        </tr>
        </thead>
        <tbody>
        {/*{data.map((item, index) => (*/}
        {/*    <tr key={index}>*/}
        {/*        {titles.map((title, index) => (*/}
        {/*            <td key={index}>{item[title]}</td>*/}
        {/*        ))}*/}
        {/*    </tr>*/}
        {/*))}*/}
        <tr>
            <td>Bagietka</td>
            <td>5</td>
            <td>Sztuk</td>
        </tr>
        <tr>
            <td>Bagietka</td>
            <td>5</td>
            <td>Sztuk</td>
        </tr>
        </tbody>
    </StyledTable>
);

export default TableMarkup;
