import React from 'react';
import ListItem from "../molecules/ListItem";
import styled from "styled-components";
import {FormattedMessage} from "react-intl";
import Button from "../atoms/Button";

const UlWrapper = styled.ul`
    width: 75vw;
    padding: 0;
    margin: 40px auto 0;
    @media (max-width: ${({theme}) => theme.mobile}) {
       width: 100vw;
       font-size: 10px;
       margin: 10px auto 0;
    }
`

const InfoWrapper = styled.h1`
     margin: 0 auto;
     height: 70px;
     display: flex;
     width: 75vw;
     align-items: center;
     justify-content: center;
     background-color: ${({theme}) => theme.colors.blue};
     color: ${({theme}) => theme.colors.white};
`

const CategoryWrapper = styled.div`
    margin: 0 auto;
    height: 70px;
    display: flex;
    width: 75vw;
    align-items: center;
    justify-content: center;
    background-color: ${({theme}) => theme.colors.blue};
    color: ${({theme}) => theme.colors.white};
    @media (max-width: ${({theme}) => theme.mobile}) {
        width: 100vw;
    }
`


const List = ({items, deleteItem, decreaseQuantity, increaseQuantity, editName, editItem, toEdit,parameter}) => {
    return (
        <>
            <CategoryWrapper>
                {parameter ? parameter : "all categories"}
                {/*<FormattedMessage id="baking"/>*/}
            </CategoryWrapper>
            <Button to={'/'}> Back</Button>

            <UlWrapper>

                {/*{console.log(parameter)}*/}

                {

                    // parameter ?

                        items.map(item => (
                            <ListItem {...item}
                                      key={item.id}
                                      deleteItem={() => deleteItem(item.id)}
                                      toEdit={toEdit}
                                      editItem={editItem}
                                      decreaseQuantity={() => decreaseQuantity(item)}
                                      increaseQuantity={() => increaseQuantity(item)}
                                      editName={() => editName(item)}
                            />
                        ))
                    // :
                    //
                    //
                    //     items.filter(item => item.category === parameter).map(item => (
                    //
                    //         <ListItem {...item}
                    //                   key={item.id}
                    //                   deleteItem={() => deleteItem(item.id)}
                    //                   toEdit={toEdit}
                    //                   editItem={editItem}
                    //                   decreaseQuantity={() => decreaseQuantity(item)}
                    //                   increaseQuantity={() => increaseQuantity(item)}
                    //                   editName={() => editName(item)}
                    //         />
                    //     ))
                }
            </UlWrapper>
        </>)
};


export default List;
