import React from 'react';
import styled from 'styled-components';
import ButtonIcon from '../atoms/Button'
import bag from '../../asstets/img/bag.svg'

const StyledModalWrapper = styled.div`
  background-color: white;
  //padding: 10px;
  position: fixed;
  top: 0;
  right: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(255, 255, 255, 0.9);
  backdrop-filter: grayscale(1) blur(2px);
  z-index: 2;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StyledModalHeader = styled.div`
  width: 100%;
  height: 100%;
  background-color: ${({theme}) => theme.colors.blue};;
  border-radius: 10px 10px 0 0;
  display: flex;
  align-items: center;
  justify-content: center;
  color: black;
  font-weight: bold;
  font-size: 14px;
`;

const StyledModalCard = styled.div`
  width: 60vw;
  height: 50vh;
  background-color: white;
  border-radius: 0 0 10px 10px;
  box-shadow: 0 30px 60px -5px hsla(0, 0%, 0%, 0.2);
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: 100px auto 80px;
  justify-items: center;
`;

const StyledContentModal = styled.div`
  width: 90%;
  padding: 20px;
`;

const StyledTitle = styled.div`
  text-align: center;
  justify-content: center;
`

// const Modal = ({closeModalFn, children, btn, title = "title"}) => (
const Modal = () => (
    <StyledModalWrapper>
        <StyledModalCard>
            <StyledModalHeader>
                {/*{title}*/}
                <StyledTitle>
                    Title
                </StyledTitle>
            </StyledModalHeader>
            <StyledContentModal>
                {/*{children}*/}
                Nothing
                <ButtonIcon
                    icon={bag}

                    onClick={() => window.alert("x")}>XX</ButtonIcon>
            </StyledContentModal>
            {/*{btn}*/}
            Noghing
        </StyledModalCard>
    </StyledModalWrapper>
);

export default Modal
