const theme = {
    colors: {
        yellow: '#FDE24F',
        pink: '#FF5470',
        green: '#00EBC7',
        darkblue:'#00214D',
        white: '#FFFFFE'
    },
    fontFamily: {
        primary: `'Red Hat Display', sans-serif`,
        heading: `'Reem Kufi', sans-serif`,
    },
};

export default theme;
