                                    </StyledLabel>
                                    <Select
                                        onChange={this.handleInputChange}
                                        name="unit"
                                        // value={this.state.unit}
                                        value={values.unit}
                                        placeholder=""
                                    >
                                        <option value="Wybierz jednostkę..." label="Choose unit..."/>
