import React, {useContext} from 'react';
import logo from '../../asstets/img/logo.svg';
import styled from "styled-components";
import {NavLink} from "react-router-dom";
import settings from "../../asstets/img/settings.svg";
// import theme from "../../theme/theme";
import {auth} from "../../firebase/firebase";
import {AuthContext} from "../../firebase/Auth";

const HeaderWrapper = styled.nav`
 @media (max-width: ${({theme}) => theme.mobile}) {
        //background-color: ${({theme}) => theme.colors.pink};
    display: none;
    }
  display: flex;
  justify-content: space-between; 
  align-items: center;
  height: 70px;
  background-color: ${({theme}) => theme.colors.blue};
  margin-bottom: 10px;
`

const HamburgerIcon = styled.div`

`

const StyledLogoLink = styled.div`
    display: block;
    width: 67px;
    height: 67px;
    background-image: url(${logo});
    background-repeat: no-repeat;
    background-position: 50% 50%;
    border: none;
`;

const List = styled.li`
    @media (max-width: ${({theme}) => theme.mobile}) {
        background-color: ${({theme}) => theme.colors.pink};
    }
    display: flex;
    margin: 0; 
    padding: 0;
`
const ListItem = styled.li`
  list-style: none;
  display: flex;
  //margin: 50px;
  height: 70px;
  width: 230px;
  text-decoration: none;
  justify-content: center;
  align-items: center;
  color: ${({theme}) => theme.colors.white};
  &.active {
    background-color: ${({theme}) => theme.colors.darkblue};
  }
`


const StyledBurger = styled.button`
  position: absolute;
  top: 5%;
  left: 2rem;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 2rem;
  height: 2rem;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
  z-index: 10;

  &:focus {
    outline: none;
  }

  div {
    width: 2rem;
    height: 0.25rem;
    background: ${({ open }) => open ? '#0D0C1D' : '#EFFFFA'};
    border-radius: 10px;
    transition: all 0.3s linear;
    position: relative;
    transform-origin: 1px;

    :first-child {
      transform: ${({ open }) => open ? 'rotate(45deg)' : 'rotate(0)'};
    }

    :nth-child(2) {
      opacity: ${({ open }) => open ? '0' : '1'};
      transform: ${({ open }) => open ? 'translateX(20px)' : 'translateX(0)'};
    }

    :nth-child(3) {
      transform: ${({ open }) => open ? 'rotate(-45deg)' : 'rotate(0)'};
    }
  }
`


const Header = () => {
    const {currentUser} = useContext(AuthContext);
    return (
        <>
            <StyledBurger open={open} onClick={() => setOpen(!open)}>
                <div/>
                <div/>
                <div/>
            </StyledBurger>

            currentUser ?
            <HeaderWrapper>
                <List>
                    <ListItem as={NavLink} exact to="/"><StyledLogoLink/></ListItem>
                    <ListItem as={NavLink} exact to="/" activeclass="active">Sprawdź zapasy</ListItem>
                    <ListItem as={NavLink} to="/add" activeclass="active">Dodaj produkt</ListItem>
                    <ListItem as={NavLink} to="/list" activeclass="active">Lista zakupów</ListItem>
                    {/*<ListItem>You are logged as {(auth.currentUser.email)}</ListItem>*/}
                    <ListItem as={NavLink} to="/settings" activeclass="active"><img src={settings}
                                                                                    alt="Settings icon"/></ListItem>
                </List>
            </HeaderWrapper> : null
        </>
    )
};

export default Header;
